input_lines = input.splitlines()
xmas = "XMAS"
xmas_count = 0

for i in range(len(input_lines)):
    for j in range(len(input_lines[0])):
        if j <= len(input_lines[0])-3 and input_lines[i][j:j+4] == xmas: # horizontal
            xmas_count += 1
        if j <= len(input_lines[0])-3 and input_lines[i][j:j+4] == xmas[::-1]: # horizontal reverse
            xmas_count += 1
        if j == 3 and input_lines[i][j+4::-1] == xmas: # horizontal reverse edge case
            xmas_count += 1
        if i <= len(input_lines)-3 and "".join([row[j] for row in input_lines[i:i+4]]) == xmas: # vertical down
            xmas_count += 1
        if i <= len(input_lines) - 3 and "".join([row[j] for row in input_lines[i:i + 4]]) == xmas[::-1]:  # vertical up
            xmas_count += 1
        if i <= len(input_lines) - 4 and j <= len(input_lines[0]) - 4:
            if "".join([input_lines[i][j], input_lines[i+1][j+1], input_lines[i+2][j+2],
                        input_lines[i+3][j+3]]) == xmas: # horizontal down
                xmas_count += 1
            if "".join([input_lines[i][j], input_lines[i + 1][j + 1], input_lines[i + 2][j + 2],
                        input_lines[i + 3][j + 3]]) == xmas[::-1]: # horizontal down reverse
                xmas_count += 1
            if "".join([input_lines[i+3][j], input_lines[i+2][j+1], input_lines[i+1][j+2],
                        input_lines[i][j+3]]) == xmas: # horizontal up
                xmas_count += 1
            if "".join([input_lines[i + 3][j], input_lines[i + 2][j + 1], input_lines[i + 1][j + 2],
                        input_lines[i][j + 3]]) == xmas[::-1]:  # horizontal up reverse
                xmas_count += 1

print(f"The number of times 'XMAS' appears is {xmas_count}")

# Part 2

cross_mass_count = 0

def check_x_mas(i, j):
    print(f"At {i},{j} theres a cross of {"".join([input_lines[i][j], input_lines[i+1][j+1], input_lines[i+2][j+2]])} "
          f"and {"".join([input_lines[i + 2][j], input_lines[i + 1][j + 1], input_lines[i + 0][j + 2]])}")
    if "".join([input_lines[i][j], input_lines[i+1][j+1], input_lines[i+2][j+2]]) in ["MAS", "SAM"]:
        if "".join([input_lines[i+2][j], input_lines[i+1][j+1], input_lines[i+0][j+2]]) in ["MAS", "SAM"]:
            print(f"There's a MAS cross at {i},{j}")
            return True

for i in range(len(input_lines)-2):
    for j in range(len(input_lines[0])-2):
        if check_x_mas(i, j):
            cross_mass_count += 1

print(f"The number of times 'MAS' crosses appears is {cross_mass_count}")
